const Instruction = () => {
	return (
		<div>
			<span class="font-medium text-sm text-gray-500 font-mono mb-3 dark:text-neutral-400">
				Instruction
			</span>
			<ul class="list-disc list-inside text-gray-800 dark:text-white">
				<li>Select a surah you want to memorize</li>
				<li>Start with 3 to 5 verses at a time</li>
				<li>Repeat each verse 3 times and all selected verses 5 times</li>
				<li>
					Listen first two times and on the thrid time, recite with the audio
				</li>
				<li>Keep repeating until you can recite the verses by youself</li>
			</ul>
		</div>
	);
};

export default Instruction;
