import React, {useState, useRef, useEffect} from "react";

const AudioPlayer = ({audioFiles, playCount}) => {
	const [currentFileIndex, setCurrentFileIndex] = useState(0);
	const [playbackCount, setPlaybackCount] = useState(0);
	const [isPlaying, setIsPlaying] = useState(false);
	const audioRef = useRef(null);

	useEffect(() => {
		if (isPlaying) {
			audioRef.current.play();
		} else {
			audioRef.current.pause();
		}
	}, [isPlaying, currentFileIndex]);

	const handleEnded = () => {
		if (playCount === Infinity || playbackCount + 1 < playCount) {
			setPlaybackCount(playbackCount + 1);
			audioRef.current.play();
		} else {
			setPlaybackCount(0);
			const nextIndex = (currentFileIndex + 1) % audioFiles.length;
			setCurrentFileIndex(nextIndex);
		}
	};

	const handlePlayPause = () => {
		setIsPlaying(!isPlaying);
	};

	const handleNext = () => {
		setPlaybackCount(0);
		setCurrentFileIndex((currentFileIndex + 1) % audioFiles.length);
	};

	const handlePrev = () => {
		setPlaybackCount(0);
		setCurrentFileIndex(
			(currentFileIndex - 1 + audioFiles.length) % audioFiles.length
		);
	};

	return (
		<div>
			<audio
				ref={audioRef}
				src={audioFiles[currentFileIndex]}
				onEnded={handleEnded}
			/>
			<button onClick={handlePlayPause}>{isPlaying ? "Pause" : "Play"}</button>
			<button onClick={handlePrev}>Previous</button>
			<button onClick={handleNext}>Next</button>
		</div>
	);
};

export default AudioPlayer;
