// src/components/AudioFileSelector.js

import React, {useState} from "react";

const AudioFileSelector = ({album, onPlay}) => {
	const [selectedFiles, setSelectedFiles] = useState([]);
	const [fileRepeatCount, setFileRepeatCount] = useState(1);
	const [albumRepeatCount, setAlbumRepeatCount] = useState(1);

	const handleFileSelect = (event, file) => {
		const newSelectedFiles = [...selectedFiles];
		if (event.target.checked) {
			newSelectedFiles.push(file);
		} else {
			const index = newSelectedFiles.indexOf(file);
			if (index > -1) {
				newSelectedFiles.splice(index, 1);
			}
		}
		setSelectedFiles(newSelectedFiles);
	};

	const handleFileRepeatCountChange = (event) => {
		setFileRepeatCount(Number(event.target.value));
	};

	const handleAlbumRepeatCountChange = (event) => {
		setAlbumRepeatCount(Number(event.target.value));
	};

	const handlePlayClick = () => {
		onPlay(selectedFiles, fileRepeatCount, albumRepeatCount);
	};

	return (
		<div>
			<h3>{album.name}</h3>
			<div>
				<h4>Select Ayas</h4>
				{album.audioFiles.map((file) => (
					<div key={file.id}>
						<input
							type="checkbox"
							id={`file-${file.id}`}
							onChange={(e) => handleFileSelect(e, file)}
						/>
						<label htmlFor={`file-${file.id}`}>{file.name}</label>
					</div>
				))}
			</div>
			<div>
				<label>
					Repeat Each Aya:
					<input
						type="number"
						value={fileRepeatCount}
						onChange={handleFileRepeatCountChange}
						min="1"
					/>
				</label>
			</div>
			<div>
				<label>
					Repeat Selected Ayas:
					<input
						type="number"
						value={albumRepeatCount}
						onChange={handleAlbumRepeatCountChange}
						min="1"
					/>
				</label>
			</div>
			<button onClick={handlePlayClick}>Set Repeat Options</button>
		</div>
	);
};

export default AudioFileSelector;
