import React, {useState} from "react";
import AudioFileSelector from "./components/AudioFileSelector";
import AudioPlayer from "./components/AudioPlayer";

const App = () => {
	const [selectedAlbum, setSelectedAlbum] = useState(null);
	const [selectedFiles, setSelectedFiles] = useState([]);
	const [fileRepeatCount, setFileRepeatCount] = useState(1);
	const [albumRepeatCount, setAlbumRepeatCount] = useState(1);

	const handleAlbumSelect = (album) => {
		setSelectedAlbum(album);
		setSelectedFiles([]);
	};

	const handleSetRepeatOptions = (files, fileRepeatCount, albumRepeatCount) => {
		setSelectedFiles(files);
		setFileRepeatCount(fileRepeatCount);
		setAlbumRepeatCount(albumRepeatCount);
	};

	const handleStopPlayback = () => {
		setSelectedFiles([]);
	};

	return (
		<div className="app">
			<div className="flex flex-col md:flex-row gap-6">
				<div className="md:w-1/3">
					{/* Replace this with your album list component */}
					{albums.map((album) => (
						<div
							key={album.id}
							onClick={() => handleAlbumSelect(album)}
							className="cursor-pointer"
						>
							{album.name}
						</div>
					))}
				</div>
				<div className="md:w-2/3">
					{selectedAlbum && (
						<AudioFileSelector
							album={selectedAlbum}
							onSetRepeatOptions={handleSetRepeatOptions}
							fileRepeatCount={fileRepeatCount}
							albumRepeatCount={albumRepeatCount}
							onStopPlayback={handleStopPlayback}
						/>
					)}
				</div>
			</div>
			{selectedFiles.length > 0 && (
				<AudioPlayer
					audioFiles={selectedFiles}
					fileRepeatCount={fileRepeatCount}
					albumRepeatCount={albumRepeatCount}
					onStopPlayback={handleStopPlayback}
				/>
			)}
		</div>
	);
};

export default App;
