import "./styles.css";
import React, {useState} from "react";
import AudioPlayer from "./AudioPlayer";
import AudioControls from "./AudioControls";
import surah108 from "./108/108001.mp3";

const App = () => {
	const [audioFiles, setAudioFiles] = useState([]);
	const [playCount, setPlayCount] = useState(5);

	const audioList = [
		{name: "Audio 1", url: surah108},
		{name: "Audio 2", url: "audio2.mp3"},
		{name: "Audio 3", url: "audio3.mp3"},
		// Add more audio files as needed
	];

	const handlePlay = (files, count) => {
		setAudioFiles(files);
		setPlayCount(count);
	};

	return (
		<div>
			<AudioControls audioList={audioList} onPlay={handlePlay} />
			{audioFiles.length > 0 && (
				<AudioPlayer
					audioFiles={audioFiles.map((file) => file.url)}
					playCount={playCount}
				/>
			)}
		</div>
	);
};

export default App;
