// src/components/AudioFileSelector.js

import "../styles.css";

import React, {useState, useEffect} from "react";

const AudioFileSelector = ({
	album,
	onSetRepeatOptions,
	fileRepeatCount,
	albumRepeatCount,
}) => {
	const [selectedFiles, setSelectedFiles] = useState([]);
	const [localFileRepeatCount, setLocalFileRepeatCount] =
		useState(fileRepeatCount);
	const [localAlbumRepeatCount, setLocalAlbumRepeatCount] =
		useState(albumRepeatCount);

	useEffect(() => {
		setSelectedFiles([]);
		setLocalFileRepeatCount(fileRepeatCount);
		setLocalAlbumRepeatCount(albumRepeatCount);
	}, [album]);

	const handleFileSelect = (event, file) => {
		const newSelectedFiles = [...selectedFiles];
		if (event.target.checked) {
			newSelectedFiles.push(file);
		} else {
			const index = newSelectedFiles.indexOf(file);
			if (index > -1) {
				newSelectedFiles.splice(index, 1);
			}
		}
		setSelectedFiles(newSelectedFiles);
	};

	const handleFileRepeatCountChange = (event) => {
		setLocalFileRepeatCount(Number(event.target.value));
	};

	const handleAlbumRepeatCountChange = (event) => {
		setLocalAlbumRepeatCount(Number(event.target.value));
	};

	const handleSetRepeatOptionsClick = () => {
		const sortedFiles = [...selectedFiles].sort((a, b) => a.id - b.id);
		onSetRepeatOptions(
			sortedFiles,
			localFileRepeatCount,
			localAlbumRepeatCount
		);
	};

	return (
		<div className="flex flex-col">
			{/* <h3 className="text-lg font-semibold mb-4">{album.name}</h3> */}
			<div className="mb-4">
				{/* <h4 className="text-sm font-medium mb-2">Select Audio Files</h4> */}
				<ul className="flex flex-col">
					{album.audioFiles.map((file) => (
						<li
							key={file.id}
							className="inline-flex items-center gap-x-2 py-3 px-4 text-sm font-medium bg-white border text-gray-800 -mt-px first:rounded-t-lg first:mt-0 last:rounded-b-lg"
						>
							<div className="relative flex items-start w-full">
								<div className="flex items-center h-5">
									<input
										id={`file-${file.id}`}
										type="checkbox"
										className="border-gray-200 rounded disabled:opacity-50"
										checked={selectedFiles.includes(file)}
										onChange={(e) => handleFileSelect(e, file)}
									/>
								</div>
								<label
									htmlFor={`file-${file.id}`}
									className="ms-3.5 block w-full text-base text-gray-600"
								>
									{file.name}
								</label>
							</div>
						</li>
					))}
				</ul>
			</div>
			<div className="sticky bottom-0 bg-white border-t border-gray-200 py-2 px-3">
				<div className="py-2 px-3 bg-white border border-gray-200 rounded-lg mb-4">
					<div className="w-full flex justify-between items-center gap-x-5">
						<div className="grow">
							<span className="block text-xs text-gray-500">
								Repeat Each File
							</span>
							<input
								className="w-full p-0 bg-transparent border-0 text-gray-800 focus:ring-0 [&::-webkit-inner-spin-button]:appearance-none [&::-webkit-outer-spin-button]:appearance-none"
								style={{"-moz-appearance": "textfield"}}
								type="number"
								value={localFileRepeatCount}
								onChange={handleFileRepeatCountChange}
								min="1"
								aria-roledescription="Number field"
							/>
						</div>
						<div className="flex justify-end items-center gap-x-1.5">
							<button
								type="button"
								className="size-6 inline-flex justify-center items-center gap-x-2 text-base font-medium rounded-full border border-gray-200 bg-white text-gray-800 shadow-sm hover:bg-gray-50 focus:outline-none focus:bg-gray-50 disabled:opacity-50 disabled:pointer-events-none"
								aria-label="Decrease"
								onClick={() =>
									setLocalFileRepeatCount((prev) => Math.max(prev - 1, 1))
								}
							>
								<svg
									className="shrink-0 size-3.5"
									xmlns="http://www.w3.org/2000/svg"
									width="24"
									height="24"
									viewBox="0 0 24 24"
									fill="none"
									stroke="currentColor"
									strokeWidth="2"
									strokeLinecap="round"
									strokeLinejoin="round"
								>
									<path d="M5 12h14"></path>
								</svg>
							</button>
							<button
								type="button"
								className="size-6 inline-flex justify-center items-center gap-x-2 text-base font-medium rounded-full border border-gray-200 bg-white text-gray-800 shadow-sm hover:bg-gray-50 focus:outline-none focus:bg-gray-50 disabled:opacity-50 disabled:pointer-events-none"
								aria-label="Increase"
								onClick={() => setLocalFileRepeatCount((prev) => prev + 1)}
							>
								<svg
									className="shrink-0 size-3.5"
									xmlns="http://www.w3.org/2000/svg"
									width="24"
									height="24"
									viewBox="0 0 24 24"
									fill="none"
									stroke="currentColor"
									strokeWidth="2"
									strokeLinecap="round"
									strokeLinejoin="round"
								>
									<path d="M5 12h14"></path>
									<path d="M12 5v14"></path>
								</svg>
							</button>
						</div>
					</div>
				</div>
				<div className="py-2 px-3 bg-white border border-gray-200 rounded-lg mb-4">
					<div className="w-full flex justify-between items-center gap-x-5">
						<div className="grow">
							<span className="block text-xs text-gray-500">Repeat Album</span>
							<input
								className="w-full p-0 bg-transparent border-0 text-gray-800 focus:ring-0 [&::-webkit-inner-spin-button]:appearance-none [&::-webkit-outer-spin-button]:appearance-none"
								style={{"-moz-appearance": "textfield"}}
								type="number"
								value={localAlbumRepeatCount}
								onChange={handleAlbumRepeatCountChange}
								min="1"
								aria-roledescription="Number field"
								disabled={selectedFiles.length <= 1}
							/>
						</div>
						<div className="flex justify-end items-center gap-x-1.5">
							<button
								type="button"
								className="size-6 inline-flex justify-center items-center gap-x-2 text-sm font-medium rounded-full border border-gray-200 bg-white text-gray-800 shadow-sm hover:bg-gray-50 focus:outline-none focus:bg-gray-50 disabled:opacity-50 disabled:pointer-events-none"
								aria-label="Decrease"
								onClick={() =>
									setLocalAlbumRepeatCount((prev) => Math.max(prev - 1, 1))
								}
								disabled={selectedFiles.length <= 1}
							>
								<svg
									className="shrink-0 size-3.5"
									xmlns="http://www.w3.org/2000/svg"
									width="24"
									height="24"
									viewBox="0 0 24 24"
									fill="none"
									stroke="currentColor"
									strokeWidth="2"
									strokeLinecap="round"
									strokeLinejoin="round"
								>
									<path d="M5 12h14"></path>
								</svg>
							</button>
							<button
								type="button"
								className="size-6 inline-flex justify-center items-center gap-x-2 text-sm font-medium rounded-full border border-gray-200 bg-white text-gray-800 shadow-sm hover:bg-gray-50 focus:outline-none focus:bg-gray-50 disabled:opacity-50 disabled:pointer-events-none"
								aria-label="Increase"
								onClick={() => setLocalAlbumRepeatCount((prev) => prev + 1)}
								disabled={selectedFiles.length <= 1}
							>
								<svg
									className="shrink-0 size-3.5"
									xmlns="http://www.w3.org/2000/svg"
									width="24"
									height="24"
									viewBox="0 0 24 24"
									fill="none"
									stroke="currentColor"
									strokeWidth="2"
									strokeLinecap="round"
									strokeLinejoin="round"
								>
									<path d="M5 12h14"></path>
									<path d="M12 5v14"></path>
								</svg>
							</button>
						</div>
					</div>
				</div>
				<button
					className=" w-full py-4 px-4 bg-blue-500 text-white text-base font-medium rounded-lg shadow-sm hover:bg-blue-600 focus:outline-none focus:bg-blue-600 disabled:opacity-50 disabled:pointer-events-none"
					onClick={handleSetRepeatOptionsClick}
					disabled={selectedFiles.length === 0}
				>
					Start
				</button>
			</div>
		</div>
	);
};

export default AudioFileSelector;
