// src/components/AudioPlayerSelector.js

import React, {useState, useRef, useEffect} from "react";

const AudioPlayerSelector = ({album, onStopPlayback, setPlaybackKey}) => {
	const [selectedFiles, setSelectedFiles] = useState([]);
	const [localFileRepeatCount, setLocalFileRepeatCount] = useState(1);
	const [localAlbumRepeatCount, setLocalAlbumRepeatCount] = useState(1);
	const [isPlaying, setIsPlaying] = useState(false);
	const [currentFileIndex, setCurrentFileIndex] = useState(0);
	const [currentAlbumRepeat, setCurrentAlbumRepeat] = useState(0);
	const [currentFileRepeat, setCurrentFileRepeat] = useState(0);
	const audioRef = useRef(null);

	useEffect(() => {
		setSelectedFiles([]);
		setLocalFileRepeatCount(1);
		setLocalAlbumRepeatCount(1);
	}, [album]);

	const handleFileSelect = (event, file) => {
		const newSelectedFiles = [...selectedFiles];
		if (event.target.checked) {
			newSelectedFiles.push(file);
		} else {
			const index = newSelectedFiles.indexOf(file);
			if (index > -1) {
				newSelectedFiles.splice(index, 1);
			}
		}
		setSelectedFiles(newSelectedFiles);
	};

	const handleFileRepeatCountChange = (event) => {
		setLocalFileRepeatCount(Number(event.target.value));
	};

	const handleAlbumRepeatCountChange = (event) => {
		setLocalAlbumRepeatCount(Number(event.target.value));
	};

	const handleSetRepeatOptionsClick = () => {
		const sortedFiles = [...selectedFiles].sort((a, b) => a.id - b.id);
		setIsPlaying(true);
		setCurrentFileIndex(0);
		setCurrentAlbumRepeat(0);
		setCurrentFileRepeat(0);
		setPlaybackKey((prevKey) => prevKey + 1); // Update the key to restart playback
	};

	useEffect(() => {
		if (isPlaying) {
			audioRef.current.play();
		} else {
			audioRef.current.pause();
		}
	}, [isPlaying, currentFileIndex]);

	useEffect(() => {
		setCurrentFileIndex(0);
		setCurrentAlbumRepeat(0);
		setCurrentFileRepeat(0);
	}, [selectedFiles, localFileRepeatCount, localAlbumRepeatCount]);

	const handleEnded = () => {
		if (currentFileRepeat + 1 < localFileRepeatCount) {
			setCurrentFileRepeat(currentFileRepeat + 1);
			audioRef.current.play();
		} else {
			setCurrentFileRepeat(0);
			if (currentFileIndex + 1 < selectedFiles.length) {
				setCurrentFileIndex(currentFileIndex + 1);
			} else if (currentAlbumRepeat + 1 < localAlbumRepeatCount) {
				setCurrentFileIndex(0);
				setCurrentAlbumRepeat(currentAlbumRepeat + 1);
			} else {
				onStopPlayback();
			}
		}
	};

	const handleStopPlayback = () => {
		setIsPlaying(false);
	};

	return (
		<div>
			<h3>{album.name}</h3>
			<div>
				<h4>Select Audio Files</h4>
				{album.audioFiles.map((file) => (
					<div key={file.id}>
						<input
							type="checkbox"
							id={`file-${file.id}`}
							checked={selectedFiles.includes(file)}
							onChange={(e) => handleFileSelect(e, file)}
						/>
						<label htmlFor={`file-${file.id}`}>{file.name}</label>
					</div>
				))}
			</div>
			<div>
				<label>
					Repeat each file:
					<input
						type="number"
						value={localFileRepeatCount}
						onChange={handleFileRepeatCountChange}
						min="1"
					/>
				</label>
			</div>
			<div>
				<label>
					Repeat album:
					<input
						type="number"
						value={localAlbumRepeatCount}
						onChange={handleAlbumRepeatCountChange}
						min="1"
						disabled={selectedFiles.length <= 1}
					/>
				</label>
			</div>
			<button
				onClick={handleSetRepeatOptionsClick}
				disabled={selectedFiles.length === 0}
			>
				Play
			</button>
			<button onClick={handleStopPlayback}>Stop</button>
			{isPlaying && (
				<div>
					<audio
						ref={audioRef}
						src={selectedFiles[currentFileIndex]?.url}
						onEnded={handleEnded}
						autoPlay={isPlaying}
					/>
				</div>
			)}
		</div>
	);
};

export default AudioPlayerSelector;
