// src/App.js

import React, {useState} from "react";
import AudioPlayerSelector from "./components/AudioPlayerSelector";

const albumsData = [
	{
		id: 1,
		name: "Album 1",
		audioFiles: Array.from({length: 10}, (_, i) => ({
			id: i + 1,
			name: `Track ${i + 1}`,
			url: `https://everyayah.com/data/Alafasy_128kbps/00100${i + 1}.mp3`,
		})),
	},
	{
		id: 2,
		name: "Album 2",
		audioFiles: Array.from({length: 10}, (_, i) => ({
			id: i + 11,
			name: `Track ${i + 1}`,
			url: `https://everyayah.com/data/Alafasy_128kbps/00101${i + 1}.mp3`,
		})),
	},
	// Add more albums as needed
];

function App() {
	const [selectedAlbum, setSelectedAlbum] = useState(albumsData[0]);

	const handleAlbumChange = (event) => {
		const selectedId = Number(event.target.value);
		const album = albumsData.find((a) => a.id === selectedId);
		setSelectedAlbum(album);
	};

	return (
		<div className="App">
			<h1>Audio Player App</h1>
			<label htmlFor="album-select">Select Album: </label>
			<select
				id="album-select"
				onChange={handleAlbumChange}
				value={selectedAlbum.id}
			>
				{albumsData.map((album) => (
					<option key={album.id} value={album.id}>
						{album.name}
					</option>
				))}
			</select>
			{selectedAlbum && <AudioPlayerSelector album={selectedAlbum} />}
		</div>
	);
}

export default App;
