// src/components/AudioPlayer.js
import "../styles.css";
import React, {useState} from "react";
import AlbumList from "./AlbumList";
import AudioFileSelector from "./AudioFileSelector";

const AlbumPlayer = ({albums}) => {
	const [selectedAlbum, setSelectedAlbum] = useState(null);
	const [repeatOptions, setRepeatOptions] = useState({
		files: [],
		fileRepeatCount: 1,
		albumRepeatCount: 1,
	});

	const handleSelectAlbum = (albumId) => {
		const album = albums.find((album) => album.id === albumId);
		setSelectedAlbum(album);
	};

	const handleSetRepeatOptions = (files, fileRepeatCount, albumRepeatCount) => {
		setRepeatOptions({files, fileRepeatCount, albumRepeatCount});
		// Logic to start playback
	};

	const handleStopPlayback = () => {
		// Logic to stop playback
		console.log("Stop playback triggered");
	};

	return (
		<div className="p-4">
			<AlbumList albums={albums} onSelectAlbum={handleSelectAlbum} />
			{selectedAlbum && (
				<AudioFileSelector
					album={selectedAlbum}
					onSetRepeatOptions={handleSetRepeatOptions}
					fileRepeatCount={repeatOptions.fileRepeatCount}
					albumRepeatCount={repeatOptions.albumRepeatCount}
					onStopPlayback={handleStopPlayback} // Pass the handler here
				/>
			)}
		</div>
	);
};

export default AlbumPlayer;
